import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { personalDetails, socialMediaUrl } from "../Details";

gsap.registerPlugin(ScrollTrigger);

function Hero() {
  const heroRef = useRef();
  const imageRef = useRef();
  const socialRef = useRef();
  const h11 = useRef();
  const h12 = useRef();
  const h13 = useRef();
  const descriptionRef = useRef();
  const ctaRef = useRef();

  useEffect(() => {
    const tl = gsap.timeline();

    // Enhanced initial animations with different directions and effects
    tl.fromTo(h11.current,
      { 
        x: -200, 
        y: -100, 
        opacity: 0, 
        rotation: -15,
        scale: 0.8,
        skewX: 20
      },
      { 
        x: 0, 
        y: 0, 
        opacity: 1, 
        rotation: 0,
        scale: 1,
        skewX: 0,
        duration: 1.2, 
        ease: "back.out(1.7)" 
      }
    )
    .fromTo(h12.current,
      { 
        x: 200, 
        y: 100, 
        opacity: 0, 
        rotation: 15,
        scale: 0.8,
        skewX: -20
      },
      { 
        x: 0, 
        y: 0, 
        opacity: 1, 
        rotation: 0,
        scale: 1,
        skewX: 0,
        duration: 1.2, 
        ease: "back.out(1.7)" 
      },
      "-=0.8"
    )
    .fromTo(h13.current,
      { 
        x: -150, 
        y: 150, 
        opacity: 0, 
        rotation: -10,
        scale: 0.7
      },
      { 
        x: 0, 
        y: 0, 
        opacity: 1, 
        rotation: 0,
        scale: 1,
        duration: 1, 
        ease: "elastic.out(1, 0.5)" 
      },
      "-=0.6"
    )
    .fromTo(imageRef.current,
      { 
        x: 200, 
        y: -100,
        opacity: 0, 
        scale: 0.5,
        rotation: 45,
        skewY: 20
      },
      { 
        x: 0, 
        y: 0,
        opacity: 1, 
        scale: 1,
        rotation: 0,
        skewY: 0,
        duration: 1.3, 
        ease: "back.out(1.7)" 
      },
      "-=0.7"
    )
    .fromTo(descriptionRef.current,
      { 
        x: 100, 
        y: 50, 
        opacity: 0, 
        scale: 0.9,
        rotation: 5
      },
      { 
        x: 0, 
        y: 0, 
        opacity: 1, 
        scale: 1,
        rotation: 0,
        duration: 1, 
        ease: "power2.out" 
      },
      "-=0.5"
    )
    .fromTo(ctaRef.current,
      { 
        y: 80, 
        opacity: 0, 
        scale: 0.8,
        filter: "blur(10px)"
      },
      { 
        y: 0, 
        opacity: 1, 
        scale: 1,
        filter: "blur(0px)",
        duration: 1.1, 
        ease: "back.out(1.7)" 
      },
      "-=0.4"
    );

    // Scroll animations
    gsap.fromTo(heroRef.current,
      { opacity: 1 },
      {
        opacity: 0.8,
        scrollTrigger: {
          trigger: heroRef.current,
          start: "top top",
          end: "bottom top",
          scrub: true
        }
      }
    );

    // Add floating animations for text elements
    gsap.to([h11.current, h12.current, h13.current], {
      y: -10,
      duration: 3,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1,
      stagger: 0.5
    });

    // Add subtle rotation animations
    gsap.to([h11.current, h12.current], {
      rotation: 1,
      duration: 4,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1,
      stagger: 1
    });

    // Add scale pulse for the main heading
    gsap.to(h11.current, {
      scale: 1.02,
      duration: 2,
      ease: "power2.inOut",
      yoyo: true,
      repeat: -1
    });

    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, []);

  return (
    <section 
      id="home" 
      ref={heroRef}
      className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden pt-20 md:pt-24 lg:pt-28"
    >
      {/* Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-2000"></div>
        <div className="absolute top-40 left-40 w-80 h-80 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-4000"></div>

        {/* Additional floating particles */}
        <div className="absolute top-20 left-1/4 w-2 h-2 bg-yellow-400 rounded-full animate-pulse opacity-60"></div>
        <div className="absolute top-32 right-1/3 w-1.5 h-1.5 bg-cyan-400 rounded-full animate-bounce opacity-70 animation-delay-1000"></div>
        <div className="absolute bottom-32 left-1/3 w-1 h-1 bg-green-400 rounded-full animate-ping opacity-80 animation-delay-2000"></div>
        <div className="absolute bottom-20 right-1/4 w-2.5 h-2.5 bg-orange-400 rounded-full animate-spin opacity-60 animation-delay-3000"></div>

        {/* Floating geometric shapes */}
        <div className="absolute top-1/4 right-1/6 w-4 h-4 bg-purple-400 transform rotate-45 animate-pulse opacity-40"></div>
        <div className="absolute bottom-1/4 left-1/6 w-3 h-3 bg-pink-400 transform rotate-12 animate-bounce opacity-50 animation-delay-1500"></div>
        <div className="absolute top-1/2 left-1/4 w-2 h-2 bg-blue-400 transform rotate-90 animate-spin opacity-60 animation-delay-2500"></div>
      </div>

      <div className="container mx-auto px-4 sm:px-6 py-12 md:py-16 lg:py-20 relative z-10">
        <div className="grid lg:grid-cols-2 gap-8 lg:gap-12 items-center">
          {/* Text Content */}
          <div className="text-white space-y-6 text-center lg:text-left">
            <div className="space-y-4">
              <h1
                ref={h11}
                className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl xl:text-7xl font-bold leading-tight animate-text-bounce"
              >
                Hi <br className="hidden sm:block" />
                My Name is<br className="hidden sm:block" />
              </h1>
              <h1
                ref={h12}
                className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl bg-clip-text bg-gradient text-transparent font-bold leading-tight animate-text-rotate"
              >
                {personalDetails.name.split(" ")[1] + " " + personalDetails.name.split(" ")[2]}
              </h1>
              <h2
                ref={h13}
                className="text-lg sm:text-xl md:text-2xl lg:text-3xl xl:text-4xl text-gray-300 font-medium animate-text-shimmer"
              >
                {personalDetails.tagline}
              </h2>
              <p 
                ref={descriptionRef}
                className="text-base sm:text-lg text-gray-400 max-w-lg mx-auto lg:mx-0"
              >
                Passionate software engineer crafting exceptional digital experiences for mobile and web platforms.
              </p>
            </div>

            {/* CTA Buttons */}
            <div ref={ctaRef} className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start">
              <a 
                href="#projects" 
                className="px-6 sm:px-8 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-semibold rounded-full hover:from-purple-600 hover:to-pink-600 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-center"
              >
                View My Work
              </a>
              <a 
                href="#contact" 
                className="px-6 sm:px-8 py-3 border-2 border-purple-500 text-purple-400 font-semibold rounded-full hover:bg-purple-500 hover:text-white transform hover:scale-105 transition-all duration-300 text-center"
              >
                Get In Touch
              </a>
              <a 
                href="/portfolio/resume.pdf" 
                download="Syed_Irfan_Haider_Resume.pdf"
                className="px-6 sm:px-8 py-3 bg-gradient-to-r from-blue-500 to-cyan-500 text-white font-semibold rounded-full hover:from-blue-600 hover:to-cyan-600 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-center flex items-center justify-center space-x-2"
              >
                <span>📄</span>
                <span>Download Resume</span>
              </a>
            </div>
          </div>

          {/* Profile Image */}
          <div ref={imageRef} className="flex justify-center lg:justify-end order-first lg:order-last">
            <div className="relative">
